mapper.update:
update spot_account set balance = balance - #{amount}, frozen = frozen + #{amount}
where user_id = #{userId} and currency=#{currency}
and balance >= #{amount}

update spot_account set balance = balance - #{amount}, frozen = frozen + #{amount}
where account_id = #{accountId}
and balance >= #{amount}

update spot_account set balance = balance + #{amount}, frozen = frozen - #{amount}
where user_id = #{userId} and currency=#{currency}
and frozen >= #{amount}

update spot_account set balance = balance + #{amount}, frozen = frozen - #{amount}
where account_id = #{accountId}
and frozen >= #{amount}
